script:
frappe.ui.form.on("Contact", {
onload:function(frm){
		if(frappe.route_titles["update_contact"])
		{
			frappe.confirm("change email address from "+cur_frm.doc.email_id+ " to "+frappe.route_titles["update_contact"]["email_id"]
				,function(){
					cur_frm.doc.email_id = frappe.route_titles["update_contact"]["email_id"];
					cur_frm.refresh();
					cur_frm.dirty();
					delete frappe.route_titles["update_contact"];
				},function(){
					delete frappe.route_titles["update_contact"];
				})

		}
	},
validate: function(frm) {
		var fieldlist = ["supplier","customer","user","organisation"]
		if(frappe.route_titles["create_contact"]==1&&!($.map(fieldlist,function(v){return frm.doc[v]?true:false}).indexOf(true)!=-1)){
			$.each(fieldlist,function(i,v){			
				cur_frm.set_df_property(v,"reqd",1);
			})

		} else {
			$.each(fieldlist,function(i,v){			
				cur_frm.set_df_property(v,"reqd",0);
			})
		}
},
after_save:function(frm){
		if (frappe.route_titles["create_contact"])
		{
			delete frappe.route_titles["create_contact"]
			frappe.set_route("Email Inbox");
			frappe.route();
		}
	}
});

frappe.ui.form.on('User',{
refresh:function(frm){
if (frm.doc.user_emails){
			var found =0
			for (var i = 0;i<frm.doc.user_emails.length;i++){
				if (frm.doc.email==frm.doc.user_emails[i].email_id){
					found = 1;
				}
			}
			frm.get_field("create_user_email").df.hidden = found;
			frm.refresh_field("create_user_email");
		}else{
			frm.get_field("create_user_email").df.hidden = 1
		}
		frm.refresh_field("create_user_email");

		if (frappe.route_titles["unsaved"]===1){
			delete frappe.route_titles["unsaved"];
			for ( var i=0;i<frm.doc.user_emails.length;i++){
				frm.doc.user_emails[i].idx=frm.doc.user_emails[i].idx+1;
			}
			frm.doc.email_account
		cur_frm.dirty();
		}
},
create_user_email:function(frm) {
		frappe.call({
			method: 'inbox.email_inbox.user.has_email_account',
			args: {email:cur_frm.doc.email},
			callback: function(r) {
				if (r["message"]== undefined){
					var doc = frappe.model.get_new_doc("Email Account");
					frappe.route_options = {
						"email_id": cur_frm.doc.email,
						"awaiting_password":1,
						"enable_incoming":1,
						"append_to":"Communication"
					};
					frappe.route_titles["create user account"]=cur_frm.doc.name;
					frappe.set_route("Form", "Email Account", doc.name);
				}else{
frappe.route_titles["create user account"]=cur_frm.doc.name;					
frappe.set_route("Form", "Email Account", r["message"][0]["name"]);
				}
			}
		})

	}
});

frappe.ui.form.on("Email Account", {
refresh:function(frm){
if (frm.doc.__islocal != 1) {
			if (frappe.route_titles["create user account"]) {
				var user =frappe.route_titles["create user account"];
				delete frappe.route_titles["create user account"];
				var userdoc = frappe.get_doc("User",user);
				frappe.model.with_doc("User", user, function (doc) { //route[2]
					var new_row = frappe.model.add_child(userdoc, "User Emails", "user_emails");
					new_row.email_account = cur_frm.doc.name;
					new_row.awaiting_password = cur_frm.doc.awaiting_password;
					new_row.email_id = cur_frm.doc.email_id;
					new_row.idx = 0;
					frappe.route_titles = {"unsaved": 1};
					frappe.set_route("Form", "User",user);
				});
            }
        }
}})
